        let db = { tabs: [], scripts: [] }, curI = null, saveTimeout;

        function login() { if(document.getElementById('pass').value === "0904") { document.getElementById('auth').style.display='none'; load(); } else alert("Sai pass!"); }

        async function load() {
            const r = await fetch(`${API}/get-hub`);
            db = await r.json();
            render();
        }

        function render() {
            const container = document.getElementById('sList');
            container.innerHTML = "";
            db.tabs.forEach((t, ti) => {
