                        <div class="tab-actions">
                            <button onclick="event.stopPropagation(); editT(${ti})">‚úèÔ∏è</button>
                            <button onclick="event.stopPropagation(); delT(${ti})">üóëÔ∏è</button>
                            <button onclick="event.stopPropagation(); addS('${t.Name}')">‚ûï</button>
                            <button class="btn-kh" onclick="event.stopPropagation(); quickKH('${t.Name}')">KH</button>
                        </div>
                    </div>
                    <div class="file-list">
                        ${scripts.map(s => `
                            <div class="file-item ${curI === s.originIdx ? 'active' : ''}" onclick="openE(${s.originIdx})">
                                <span>üìÑ ${s.name}</span>
                                <span onclick="event.stopPropagation(); delS(${s.originIdx})" style="color:red">‚úï</span>
                            </div>
                        `).join('')}
                    </div>
                `;
                container.appendChild(block);
            });
        }

        async function quickKH(tabName) {
            let n = prompt("T√™n Script m·ªõi:"); if(!n) return;
            let link = "";
            try {
                // T·ª± ƒë·ªông d√°n link t·ª´ Clipboard
                link = await navigator.clipboard.readText();
                if(!link || !link.includes("http")) link = prompt("Kh√¥ng t√¨m th·∫•y link, h√£y d√°n tay:");
            } catch(e) { link = prompt("L·ªói Clipboard, d√°n link tay:"); }

            if(!link) return;
            db.scripts.push({
                name: n, tab: tabName, img: DEF, 
                script: `loadstring(game:HttpGet("${link.trim()}"))()`, 
                describe: "Quick Load", ratio: "ratio-1-1"
            });
            render();
            openE(db.scripts.length - 1);
            autoSave(); // L∆∞u ngay l·∫≠p t·ª©c sau khi t·∫°o
        }

        function openE(i) {
            curI = i; const s = db.scripts[i];
            document.getElementById('editor').style.display = 'block';
            document.getElementById('name_i').value = s.name;
            document.getElementById('img_i').value = (s.img === DEF) ? "" : s.img;
            document.getElementById('script_i').value = s.script;
            document.getElementById('desc_i').value = s.describe;
            render();
        }

        // H√ÄM AUTO SAVE (T·ª± ƒë·ªông g·ª≠i l√™n server sau 1 gi√¢y ng·ª´ng g√µ)
        function autoSave() {
            if(curI === null) return;
            document.getElementById('status').innerText = "ƒêang g√µ...";

            // C·∫≠p nh·∫≠t d·ªØ li·ªáu v√†o bi·∫øn db ngay l·∫≠p t·ª©c
            db.scripts[curI].name = document.getElementById('name_i').value;
            let iv = document.getElementById('img_i').value.trim();
            db.scripts[curI].img = iv === "" ? DEF : iv;
            db.scripts[curI].script = document.getElementById('script_i').value;
            db.scripts[curI].describe = document.getElementById('desc_i').value;

            // ƒê·ª£i 1 gi√¢y sau khi ng·ª´ng g√µ m·ªõi g·ª≠i l√™n Server/GitHub
            clearTimeout(saveTimeout);
            saveTimeout = setTimeout(async () => {
                document.getElementById('status').innerText = "ƒêang l∆∞u l√™n GitHub...";
                try {
                    await fetch(`${API}/save-hub`, {
                        method: 'POST',
                        headers: {'Content-Type': 'application/json'},
                        body: JSON.stringify({ newData: db })
                    });
                    document.getElementById('status').innerText = "ƒê√£ l∆∞u t·∫•t c·∫£!";
                } catch(e) {
                    document.getElementById('status').innerText = "L·ªói l∆∞u d·ªØ li·ªáu!";
                }
            }, 1000);
        }

        function toggleTab(i) { document.getElementById(`tab-block-${i}`).classList.toggle('collapsed'); }
        function addT() { let n = prompt("T√™n Tab:"); if(n) { db.tabs.push({Name: n}); render(); autoSave(); } }
        function editT(i) { let n = prompt("T√™n m·ªõi:", db.tabs[i].Name); if(n) { let old = db.tabs[i].Name; db.tabs[i].Name = n; db.scripts.forEach(s => { if(s.tab === old) s.tab = n; }); render(); autoSave(); } }
        function delT(i) { if(confirm("X√≥a Tab?")) { let n = db.tabs[i].Name; db.tabs.splice(i,1); db.scripts = db.scripts.filter(s => s.tab !== n); render(); autoSave(); } }
        function addS(t) { let n = prompt("T√™n Script:"); if(n) { db.scripts.push({name:n, tab:t, img:DEF, script:"", ratio:"ratio-1-1"}); render(); openE(db.scripts.length-1); autoSave(); } }
        function delS(i) { if(confirm("X√≥a?")) { db.scripts.splice(i,1); document.getElementById('editor').style.display='none'; curI=null; render(); autoSave(); } }

        load();
    </script>
</body>
</html>"))()
